int32_t maxi(int32_t arr[], int n){   
    int i;
    int32_t max = arr[0];
    for (i = 1; i < n; i++){
        if (arr[i] > max){
            max = arr[i];}}
    return max;
}
int32_t mini(int32_t arr[], int n)      
{
    int i;
    int32_t min = arr[0];
    for (i = 1; i < n; i++){
        if (arr[i] < min){
            min = arr[i];}}
    return min;
}


void isr_bouton(void)
{
    Cy_GPIO_ClearInterrupt(Bouton_0_PORT, Bouton_0_NUM);
    NVIC_ClearPendingIRQ(Bouton_isr_cfg.intrSrc);
    
    
    if (currentGraph==IR_SIGNAL)
    {
        currentGraph=RED_SIGNAL;
    }
    else if (currentGraph==RED_SIGNAL)
    {
        currentGraph=IR_SIGNAL;
    }
}
/* Image buffer cache */
uint8 imageBufferCache[CY_EINK_FRAME_SIZE] = {0};


void UpdateDisplay(cy_eink_update_t updateMethod, bool powerCycle)
{
    cy_eink_frame_t* pEmwinBuffer;

    /* Get the pointer to Emwin's display buffer */
    pEmwinBuffer = (cy_eink_frame_t*)LCD_GetDisplayBuffer();

    /* Update the EInk display */
    Cy_EINK_ShowFrame(imageBufferCache, pEmwinBuffer, updateMethod, powerCycle);

    /* Copy the EmWin display buffer to the imageBuffer cache*/
    memcpy(imageBufferCache, pEmwinBuffer, CY_EINK_FRAME_SIZE);
}

void ClearScreen(void)
{
    GUI_SetColor(GUI_BLACK);
    GUI_SetBkColor(GUI_WHITE);
    GUI_Clear();
    UpdateDisplay(CY_EINK_FULL_4STAGE, true);
}

void modifDEL()
{
    if (currentPage==PAGE_LED)
    {
        if (flickLeft==true) 
        {
            courantDEL-=5;
            
        }
        if (flickRight==true)
        {
            courantDEL+=5;
            
        } 
        if (courantDEL<5) // sous l'hypothèse que le courant min = 5 et max = 50 mA 
            courantDEL = 20;
        if (courantDEL>50)
            courantDEL = 5;
    }
}

void alarmeAcc()
{
    if (currentPage==PAGE_ALARME_1)
    {
        if (flickLeft==true) 
        {
            flagAlarme = false;
            
        }
        if (flickRight==true)
        {
            flagAlarme = true;
            
        } 
    }
}
void alarmeRythme()
{
    if (currentPage==PAGE_ALARME_2)
    {
        if (flickLeft==true) // réduire la taille de l'intervalle
        {
            borneInf+=2;
            borneSup-=2;
            
        }
        if (flickRight==true) // augmenter la taille de l'alarme 
        {
            borneSup+=2;
            borneInf-=2;
            
        } 
    }
}

void drawSignal(int longueur, int intervalle, int32_t *pointeur)
{
    
    // division par 5 pour passer de int32_t à int16_t
    int32_t maximum = (maxi(pointeur, longueur))/5;
    int32_t minimum = (mini(pointeur, longueur))/5; 
     
    int16_t vecteurAffichage[500]={};
 
    int j=0;
    for (int i=0; i<500; i+=intervalle)
    {
        vecteurAffichage[j]=((((pointeur[i]/5)-minimum))*(maximum-minimum)/(2*maximum)); 
        j++;
    }
    
    pointeurAffichage = &vecteurAffichage[0];
    GUI_DrawGraph(&vecteurAffichage[0], 250, 1, 50);
    
}

void updateParameters(int param1, int param2)  // on peut utiliser cette fonction pour afficher la saturation et la fréquence
{
    char buffer[50];
    char buffer2[50];
    GUI_SetFont(GUI_FONT_10_1);
    GUI_SetTextAlign(GUI_TA_LEFT);
    GUI_DispStringAt("Frequence cardiaque (bpm): ", 10, 150); 
    sprintf(&buffer[0], "%d", param1);
    GUI_DispStringAt(buffer,155,150);
    GUI_DispStringAt("Saturation en oxygene (%): ", 10,160);
    sprintf(&buffer2[0], "%d", param2);
    GUI_DispStringAt(buffer2, 155, 160);
}

