void analyseCapsense()   
{
    if (bouton0Touched==true)
    {
        // faire back 
    }
    if (bouton1Touched==true)
    {
         
        chooseOption(); // aller dans une fonction qui analyse on est à quelle option
    }
    
    if (currentPage==PAGE_MENU)
    {
    
        if (flickLeft==true)
        {
            y-=30;
        }
        if (flickRight==true)
        {
            y+=30;
        } 
        if (y<40)
            y=130;
        if (y>130)
            y=40;
    }
}



void capSense_Task(void *pvParameters)
{
    //uint32_t     sliderGesture;
    uint32_t     gestureTimestamp = 0;
    bool changeOccured=false;
    CapSense_SetGestureTimestamp(0u);
    
    for(;;)
    {
        CapSense_ProcessAllWidgets();
        sliderPos=CapSense_GetCentroidPos(CapSense_LINEARSLIDER0_WDGT_ID);
        if(CapSense_IsWidgetActive(CapSense_BUTTON0_WDGT_ID)){
           bouton0Touched = true;
           changeOccured=true;
        }
        if(CapSense_IsWidgetActive(CapSense_BUTTON1_WDGT_ID))
        {
            bouton1Touched = true;
            changeOccured=true;
        }
        else
        {
 
                switch(CapSense_DecodeWidgetGestures
                                (CapSense_LINEARSLIDER0_WDGT_ID))
                {    
                    /* Left flick */
                    case CapSense_ONE_FINGER_FLICK_LEFT:
                        flickLeft = true;
                        changeOccured=true;
                        break;
                    /* Right flick */
                    case CapSense_ONE_FINGER_FLICK_RIGHT:
                        flickRight = true;
                        changeOccured=true;
                        break;                        
                    /* No gesture */
                    default:
                    break;   
                }
        }
        
        
        CapSense_UpdateAllBaselines();
        CapSense_ScanAllWidgets();
        gestureTimestamp += GESTURE_TIME_INCREMENT;
        CapSense_SetGestureTimestamp(gestureTimestamp);
        if(changeOccured){
            analyseCapsense(); // fonction qui analyse et réinitialise les flags 
            changeOccured=false; //reset flag after process
        }
        vTaskDelay(pdMS_TO_TICKS(50)); 
    }
    (void) pvParameters;
}



void Task_Display(void *pvParameters) 
{
    (void) pvParameters;
     Cy_SysInt_Init(&Bouton_isr_cfg, isr_bouton);
     NVIC_ClearPendingIRQ(Bouton_isr_cfg.intrSrc);
     NVIC_EnableIRQ(Bouton_isr_cfg.intrSrc); 
    initialiserGUI();
    
    drawMenu(y); 
    UpdateDisplay(CY_EINK_FULL_4STAGE, true);
    
//    //boucle pour tester la position du carré - fonctionne
//    for(int xTest=0; xTest<264; xTest+=30)  {
//        for(int yTest=0; yTest<176; yTest+=10)  {
//            GUI_Clear();
//            drawMenu(yTest);           
//            UpdateDisplay(CY_EINK_FULL_4STAGE, true);
//            vTaskDelay(pdMS_TO_TICKS(500));
//        }
//    }
    
    for(;;)
    {
        if (currentPage==PAGE_AFFICHAGE)
        {
            while(bouton0Touched==false)
            {
                GUI_Clear();
                affichage();
                UpdateDisplay(CY_EINK_FULL_4STAGE, true);
                vTaskDelay(pdMS_TO_TICKS(1000));
            }
        }
        
        
        // passer à travers les options du menu 
        if ((flickLeft || flickRight)&& currentPage==PAGE_MENU)
        {
            GUI_Clear();
            drawMenu(y);
            UpdateDisplay(CY_EINK_FULL_4STAGE, true);
            flickLeft = false;
            flickRight = false; 
        }
        
        if (bouton1Touched) // enter
        {
            // afficher la page de l'option choisie 
            GUI_Clear();
            drawPage();
            UpdateDisplay(CY_EINK_FULL_4STAGE, true);
            bouton1Touched = false;
            
        } 
        
        if ((bouton0Touched)&& currentPage != PAGE_MENU) // back to menu
        {
            currentPage = PAGE_MENU;
            GUI_Clear();
            drawMenu(y); 
            UpdateDisplay(CY_EINK_FULL_4STAGE, true);
            bouton0Touched = false;
        }
        
        if (currentPage==PAGE_LED)
        {
            while(bouton0Touched==false)
            {
                GUI_Clear();
                modifDEL();; 
                drawPageLED();
                UpdateDisplay(CY_EINK_FULL_4STAGE, true);
                flickLeft = false;
                flickRight = false;
                vTaskDelay(pdMS_TO_TICKS(1000));
            }
            
        }
        if (currentPage==PAGE_ALARME_1)
        {
            while(bouton0Touched==false)
            {
                GUI_Clear();
                alarmeAcc(); 
                drawPageALARME_Acc();
                UpdateDisplay(CY_EINK_FULL_4STAGE, true);
                flickLeft = false;
                flickRight = false;
                vTaskDelay(pdMS_TO_TICKS(1000));
            }
        }
        
        if (currentPage==PAGE_ALARME_2)
        {
            while(bouton0Touched==false)
            {
                GUI_Clear();
                alarmeRythme(); 
                drawPageALARME_Rythme();
                UpdateDisplay(CY_EINK_FULL_4STAGE, true);
                flickLeft = false;
                flickRight = false;
                vTaskDelay(pdMS_TO_TICKS(1000));
            }
        }
        
        vTaskDelay(pdMS_TO_TICKS(1000));
    }
 }   


